nuitems":[{"description":"Ability:","value":"Ward, Leap"},{"description":"Affects:","value":"Self"}],"rankitems":[{"description":"Passive MP5:","value":"4\/6\/8\/10\/12"},{"description":"Vision Range:","value":"40\/50\/60\/70\/80"},{"description":"Summon Range:","value":"65\/65\/65\/65\/70"}]}},"abilityDescription4":{"itemDescription":{"cooldown":"90s","cost":"100","description":"Scylla reveals her true nature, becoming CC immune for the next 6s, and gaining movement speed. She may make one powerful attack during this time. If she kills an enemy god with the attack, she gains another 6s and may attack again.\u000a\u000aAt max rank, the movement speed bonus doubles.","menuitems":[{"description":"Ability:","value":"Ground Target"},{"description":"Affects:","value":"Enemies"},{"description":"Damage:","value":"Magical"},{"description":"Range:","value":"60"}],"rankitems":[{"description":"Damage per Hit:","value":"400\/500\/600\/700\/800 (120% of your Magical Power)"},{"description":"Movement Speed:","value":"35\/35\/35\/35\/70%"}]}},"abilityDescription5":{"itemDescription":{"cooldown":"","cost":"","description":"Each of Scylla's abilities gain an additional effect at max rank. In addition, Scylla gains 25 Magical Power for each max rank ability.","menuitems":[{"description":"Ability:","value":"Passive"}],"rankitems":[]}},"basicAttack":{"itemDescription":{"cooldown":"","cost":"","description":"","menuitems":[{"description":"Damage:","value":"34 + 1.45\/Lvl (+20% of Magical Power)"},{"description":"Progression:","value":"None"}],"rankitems":[]}},"godAbility1_URL":"https:\/\/web2.hirez.com\/smite\/god-abilities\/sic-em.jpg","godAbility2_URL":"https:\/\/web2.hirez.com\/smite\/god-abilities\/crush.jpg","godAbility3_URL":"https:\/\/web2.hirez.com\/smite\/god-abilities\/sentinel.jpg","godAbility4_URL":"https:\/\/web2.hirez.com\/smite\/god-abilities\/im-a-monster.jpg","godAbility5_URL":"https:\/\/web2.hirez.com\/smite\/god-abilities\/quick-learner.jpg","godCard_URL":"https:\/\/w